package ManishLokesh.Neptune.Common;public class ApiFailure extends ApiResponse {    public ApiFailure(String error) {        super(ApiResponse.FAILURE, error, null);    }    public ApiFailure(String error, Object result) {        super(ApiResponse.FAILURE, error, result);    }    public ApiFailure() {        super(ApiResponse.FAILURE, "Oops! Something went wrong. Please try again later", null);    }}