package ManishLokesh.Neptune.v1.GetOrderFromIRCTC.Controller;import ManishLokesh.Neptune.AuthController.JwtUtil;import ManishLokesh.Neptune.Common.ResponseDTO;import ManishLokesh.Neptune.v1.GetOrderFromIRCTC.RequestBody.IRCTCRequestBody;import ManishLokesh.Neptune.v1.GetOrderFromIRCTC.Service.OrderService;import com.fasterxml.jackson.core.JsonProcessingException;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpHeaders;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestHeader;import org.springframework.web.bind.annotation.RestController;import java.util.Objects;@RestControllerpublic class GetIRCTCOrderController {    @Autowired    public JwtUtil jwtUtil;    @Autowired    public OrderService service;    public Logger logger = LoggerFactory.getLogger("app.irctc.order.accept");    @PostMapping("/order")    public ResponseEntity<?>getOrder(@RequestBody IRCTCRequestBody irctcRequestBody, @RequestHeader(HttpHeaders.AUTHORIZATION) String auth){        String token = auth.replace("Bearer","");        if(Objects.equals(jwtUtil.validateRole(token), "ADMIN")){            try{                logger.info("Request for " + "/order " + "requested Json" + irctcRequestBody.toString());                return this.service.OrderCapture(irctcRequestBody);            }catch (JsonProcessingException e){                return new ResponseEntity<>(new ResponseDTO("failure","Something went Wrong",null),                        HttpStatus.INTERNAL_SERVER_ERROR);            }        }        return new ResponseEntity<>(new ResponseDTO("failure","Not Authorise to Access",null),                HttpStatus.UNAUTHORIZED);    }}