package ManishLokesh.Neptune.v2.Menu.MenuResponse;public class MenuResponse {    private Long id;    private String name;    private String description;    private Double basePrice;    private Boolean isVegeterian;    private String image;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getDescription() {        return description;    }    public void setDescription(String description) {        this.description = description;    }    public Double getBasePrice() {        return basePrice;    }    public void setBasePrice(Double basePrice) {        this.basePrice = basePrice;    }    public Boolean getIsVegeterian() {        return isVegeterian;    }    public void setIsVegeterian(Boolean isVegeterian) {        this.isVegeterian = isVegeterian;    }    public String getImage() {        return image;    }    public void setImage(String image) {        this.image = image;    }}