package ManishLokesh.Neptune.v2.Orders.RequestBody.OrderPushToIRCTC;import com.fasterxml.jackson.annotation.JsonInclude;import com.fasterxml.jackson.annotation.JsonProperty;@JsonInclude(JsonInclude.Include.NON_NULL)public class CustomerInfo {    @JsonProperty("fullName")    private String fullName;    @JsonProperty("email")    private String email;    @JsonProperty("mobile")    private String mobile;    @Override    public String toString() {        return "CustomerInfo{" +                "fullName='" + fullName + '\'' +                ", email='" + email + '\'' +                ", mobile='" + mobile + '\'' +                '}';    }    public void setFullName(String fullName) {        this.fullName = fullName;    }    public void setEmail(String email) {        this.email = email;    }    public void setMobile(String mobile) {        this.mobile = mobile;    }}