package ManishLokesh.Neptune.v2.Orders.RequestBody.OrderPushToIRCTC;import com.fasterxml.jackson.annotation.JsonInclude;import com.fasterxml.jackson.annotation.JsonProperty;@JsonInclude(JsonInclude.Include.NON_NULL)public class OrderItemsInfo {    @JsonProperty("itemId")    private Long itemId;    @JsonProperty("itemName")    private String itemName;    @JsonProperty("description")    private String description;    @JsonProperty("basePrice")    private Double basePrice;    @JsonProperty("sellingPrice")    private Double sellingPrice;    @JsonProperty("taxRate")    private Double taxRate;    @JsonProperty("isVegetarian")    private Boolean isVegetarian;    @JsonProperty("quantity")    private Integer quantity;    @JsonProperty("option")    private String option;    public void setItemId(Long itemId) {        this.itemId = itemId;    }    public void setItemName(String itemName) {        this.itemName = itemName;    }    public void setDescription(String description) {        this.description = description;    }    public void setBasePrice(Double basePrice) {        this.basePrice = basePrice;    }    public void setSellingPrice(Double sellingPrice) {        this.sellingPrice = sellingPrice;    }    public void setTaxRate(Double taxRate) {        this.taxRate = taxRate;    }    public void setVegetarian(Boolean vegetarian) {        isVegetarian = vegetarian;    }    public void setQuantity(Integer quantity) {        this.quantity = quantity;    }    public void setOption(String option) {        this.option = option;    }}