package ManishLokesh.Neptune.v2.Orders.RequestBody.OrderPushToIRCTC;public class OrderItemsInfo {    private Long itemId;    private String itemName;    private String description;    private Double basePrice;    private Double sellingPrice;    private Double taxRate;    private Boolean isVegetarian;    private Integer quantity;    private String option;    public void setItemId(Long itemId) {        this.itemId = itemId;    }    public void setItemName(String itemName) {        this.itemName = itemName;    }    public void setDescription(String description) {        this.description = description;    }    public void setBasePrice(Double basePrice) {        this.basePrice = basePrice;    }    public void setSellingPrice(Double sellingPrice) {        this.sellingPrice = sellingPrice;    }    public void setTaxRate(Double taxRate) {        this.taxRate = taxRate;    }    public void setVegetarian(Boolean vegetarian) {        isVegetarian = vegetarian;    }    public void setQuantity(Integer quantity) {        this.quantity = quantity;    }    public void setOption(String option) {        this.option = option;    }}