package ManishLokesh.Neptune.v2.Orders.ResponseBody;public class OrderItemResponse {    private Long id;    private String itemName;    private boolean veg;    private String description;    private Double basePrice;    private Integer quantity;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getItemName() {        return itemName;    }    public void setItemName(String itemName) {        this.itemName = itemName;    }    public boolean isVeg() {        return veg;    }    public void setVeg(boolean veg) {        this.veg = veg;    }    public String getDescription() {        return description;    }    public void setDescription(String description) {        this.description = description;    }    public Double getBasePrice() {        return basePrice;    }    public void setBasePrice(Double basePrice) {        this.basePrice = basePrice;    }    public Integer getQuantity() {        return quantity;    }    public void setQuantity(Integer quantity) {        this.quantity = quantity;    }}