package ManishLokesh.Neptune.v2.payment;import ManishLokesh.Neptune.Common.ResponseDTO;import ManishLokesh.Neptune.v2.payment.repo.PaymentGatewayRepo;import ManishLokesh.Neptune.v2.payment.repo.PaymentMethodsRepo;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.stereotype.Service;import java.util.List;import java.util.concurrent.CompletionStage;import static ManishLokesh.Neptune.Common.Response.ApiSuccess;@Servicepublic class PaymentServiceImp implements PaymentService{    public Logger logger = LoggerFactory.getLogger("app.v2.payment.service");    @Autowired    public PaymentGatewayRepo paymentGatewayRepo;    @Autowired    public PaymentMethodsRepo paymentMethodsRepo;    @Override    public CompletionStage<ResponseEntity<ResponseDTO>> getPaymentMethodsDetail() {        logger.info("Payment available API calling......");        List<PaymentMethods> paymentMethods =  paymentMethodsRepo.findAll();        List<PaymentGateways> paymentGateways = paymentGatewayRepo.findAll();        PaymentResponse paymentResponse = new PaymentResponse(paymentGateways,"More Payment Options",paymentMethods);        logger.info("Response {}",paymentResponse.toString());        return ApiSuccess(paymentResponse);    }}